/**
 * Enqueue Google Web Fonts.
 */
function seoaal_theme_default_fonts_url() {
    $font_url = '';
    
    /*
    Translators: If there are characters in your language that are not supported
    by chosen font(s), translate this to 'off'. Do not translate into your own language.
     */ 
    if ( 'off' !== _x( 'on', 'Google font: on or off', 'seoaal' ) ) {
        $font_url = add_query_arg( 'family', urlencode( 'Roboto:400|Nunito:700&subset=latin' ), "//fonts.googleapis.com/css" );

    }
    return $font_url;
}
function seoaal_enqueue_google_web_fonts() {
	wp_enqueue_style( 'seoaal-google-fonts', seoaal_theme_default_fonts_url(), array(), null, 'all' );
}
function seoaal_default_theme_options(){
	$cur_theme = wp_get_theme();	
	if ( $cur_theme->get( 'Name' ) == 'Seoaal' || $cur_theme->get( 'Name' ) == 'Seoaal Child' ){
		$input_val = seoaal_default_theme_values();
		$seoaal_options = json_decode( $input_val, true );
		update_option( 'seoaal_options', $seoaal_options );
		seoaal_save_theme_options();
	}
}
if ( ! class_exists( 'SeoaalRedux' ) ) {
	add_action( 'wp_enqueue_scripts', 'seoaal_enqueue_google_web_fonts' );
	add_action("after_switch_theme", "seoaal_default_theme_options", 10);
}
